    <script>jQuery(document).ready(function ($) {
            var sections = $('.home-sticky_content'),
                nav = $('.sticky-nav'),
                sectionImage = $('.right >div');

            //smoothscroll
            // $('.sticky-nav a[href^="#"]').on('click', function (e) {
            //     e.preventDefault();
            //     // $(document).off("scroll");

            //     // $('.sticky-nav a').each(function () {
