    <script>jQuery(document).ready(function ($) {
            var sections = $('.home-sticky_content'),
                nav = $('.sticky-nav'),
                sectionImage = $('.right >div');

            //smoothscroll
            // $('.sticky-nav a[href^="#"]').on('click', function (e) {
            //     e.preventDefault();
            //     // $(document).off("scroll");

            //     // $('.sticky-nav a').each(function () {
            //     //     $(this).removeClass('current');
            //     // })
            //     // $(this).addClass('current');

            //     var target = this.hash,
            //         menu = target;
            //     $target = $(target);
            //     $('html, body').stop().animate({
            //         'scrollTop': $target.offset().top
            //     }, 500, 'swing', function () {
            //         window.location.hash = target;
            //         $(document).on("scroll", onScroll);
            //     });
            // });
            nav.find('a').on('click', function (e) {
                e.preventDefault();
                var $el = $(this),
                    id = $el.attr('href');
                // console.log('click',id, .offset().top);
                // 
                if ($(window).width() < 992) {
                    $('.sticky-nav a').removeClass('current');
                    $(this).addClass('current');

                }

                $('html, body').animate({
                    scrollTop: $(id).offset().top
                }, 500, 'swing', function () {
                    //         window.location.hash = target;
                    $(document).on("scroll", onScroll);
                });

                return false;
            });

            function onScroll(event) {
                var cur_pos = $(this).scrollTop();
                sections.each(function () {
                    var top = $(this).offset().top - 2,
                        bottom = top + $(this).outerHeight();
                    if ((cur_pos >= top) && (cur_pos <= bottom) && ($(window).width() > 992)) {
                        nav.find('a').removeClass('current');
                        sectionImage.find('div').removeClass('current');
                        sections.removeClass('current');
                        $(this).addClass('current');
                        nav.find('a[href="#' + $(this).attr('id') + '"]').addClass('current');
                        sectionImage.find('div[data-href="#' + $(this).attr('id') + '"]').addClass('current');
                    }
                });
            }

            $(document).on("scroll", onScroll);
            //}
        })

